var link=document.querySelector(".btn-write-us"),popup=document.querySelector(".modal-contacts"),close=document.querySelector(".modal-close"),form=popup.querySelector("form"),login=popup.querySelector("[name=name]"),email=popup.querySelector("[name=email]"),storage=localStorage.getItem("login");link.addEventListener("click",function(a){a.preventDefault();popup.classList.add("modal-contacts-show");login.focus();storage?(login.value=storage,email.focus()):login.focus()});
close.addEventListener("click",function(a){a.preventDefault();popup.classList.remove("modal-contacts-show");popup.classList.remove("modal-error")});form.addEventListener("submit",function(a){login.value&&email.value?localStorage.setItem("login",login.value):(a.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))});
window.addEventListener("keydown",function(a){27===a.keyCode&&popup.classList.contains("modal-contacts-show")&&(popup.classList.remove("modal-contacts-show"),popup.classList.remove("modal-error"))});